    private void fToCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_fToCActionPerformed
        // grab fahrenheit input as text

        String fDeg = fDegInput.getText();

        // change fahrenheit text into double
        double convertedF = Double.parseDouble(fDeg);

        // convert fahrenheit double into celsius
        double finalF1 = (convertedF - 32) * 5 / 9;

        // round celsius input to nearest tenth
        double finalF = Math.round(finalF1 * 10) / 10.0;

        // output fahrenheit converted to celsius
        cDegInput.setText(finalF + "");
    }//GEN-LAST:event_fToCActionPerformed

