    private void cToFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cToFActionPerformed
        // grab celsius input as text
        String cDeg = cDegInput.getText();

        // change celsius text into double
        double convertedC = Double.parseDouble(cDeg);

        // convert celsius double into fahrenheit 
        double finalC1 = convertedC * 9 / 5 + 32;

        // round fahrenheit output to nearest tenth
        double finalC = Math.round(finalC1) * 10 / 10.0;

        // output celsius converted to fahrenheit
        fDegInput.setText(finalC + "");
    }//GEN-LAST:event_cToFActionPerformed

